import React, { useState } from "react";

function Header({ onSearch, searchTerm }) {   // ‚úÖ Nh·∫≠n props t·ª´ cha
  const [showModal, setShowModal] = useState(null); // null | "login" | "register"

  const openModal = type => setShowModal(type);
  const closeModal = () => setShowModal(null);

  return (
    <header>
      <div className="brand">
        <div className="logo">TN</div>
        <div>
          <div>TicketNow</div>
          <div className="subtitle">Your Events, One Click Away</div>
        </div>
      </div>

      <div className="search-login">
        {/* ‚úÖ Khi ng∆∞·ªùi d√πng g√µ, g·ªçi onSearch */}
        <input
          type="text"
          placeholder="T√¨m ki·∫øm s·ª± ki·ªán..."
          className="search-input"
          onChange={(e) => onSearch(e.target.value)}  // üî• Th√™m d√≤ng n√†y
        />
        <div className="auth-links">
          <button className="auth-link" onClick={() => openModal("login")}>ƒêƒÉng nh·∫≠p</button>
          <span className="divider">|</span>
          <button className="auth-link" onClick={() => openModal("register")}>ƒêƒÉng k√Ω</button>
        </div>
      </div>

      {/* Modal Overlay */}
      {showModal && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal" onClick={e => e.stopPropagation()}>
            <button className="close-btn" onClick={closeModal}>√ó</button>
            {showModal === "login" && <h2>ƒêƒÉng nh·∫≠p</h2>}
            {showModal === "register" && <h2>ƒêƒÉng k√Ω</h2>}
            {/* Form login/register */}
            <form>
              <input type="text" placeholder="T√™n ƒëƒÉng nh·∫≠p" />
              <input type="password" placeholder="M·∫≠t kh·∫©u" />
              <button type="submit">{showModal === "login" ? "ƒêƒÉng nh·∫≠p" : "ƒêƒÉng k√Ω"}</button>
            </form>
          </div>
        </div>
      )}
    </header>
  );
}

export default Header;
